import React from 'react'
import './Main.css'
import All from '../etc/all.json'
import Book from '../components/Book.jsx'

const M_book = () => {

  let randomPick = [~~(Math.random()*20)] // 20권 중에
  
  while(randomPick.length<3) { // 3권 추천
    var sum = 0;
    var random = ~~(Math.random()*20)
    randomPick.map((e)=>e==random).map((e)=>sum+=e)
    if(sum/randomPick.length == 0){
      randomPick.push(random)
    }
  }

  return (
    <div className='center'>
      <div className='bookCase'>
        <Book All={All} randomPick={randomPick[0]}></Book>
        <Book All={All} randomPick={randomPick[1]}></Book>
        <Book All={All} randomPick={randomPick[2]}></Book>
        <Book All={All} randomPick={randomPick[3]}></Book>
        <Book All={All} randomPick={randomPick[4]}></Book>
        <Book All={All} randomPick={randomPick[5]}></Book>
      </div>
    </div>
  )
}

export default M_book
