import React, {useRef, useState} from 'react'

const Cap = () => {

  const array = []
  for(let i=1;i<91;i++){
    array.push(i)
  }
  const capArr = array.map((e)=>{
    return require(`../../img/패션img/모자/모자${e}.jpg`)
  })

  const [left,setLeft] = useState(0);
  const [dragging,setDragging] = useState(false);
  // class에 삼항연산자로 넣어서 드래그할 때 스타일을 따로 넣을 수 있다.
  const dragItem = useRef();
  const dragNode = useRef();
  let Xstart = 0;
  let Xend = 0;

  const handleDragStart = (e) =>{
    console.log('drag starting', e.screenX);
    Xstart = e.screenX;
    setDragging(true);
    dragNode.current = e.target;
    dragNode.current.addEventListener('dragend',handleDragEnd);

    
  }

  const handleDragEnd = (e) =>{
    console.log('drag ending..', e.screenX);
    Xend = e.screenX;
    setLeft(Xend-Xstart);
    setDragging(false);
    dragNode.current.removeEventListener('dragend', handleDragEnd);
    dragNode.current = null;

  }



  return (
    <div style={{position:'relative'}}>
      <div 
      draggable 
      onDragStart={(e) => {handleDragStart(e)}} 
      style={{
        height:'100%',
        width:'100%',
        position: 'absolute',
        left:left
      }}/>
      <div style={{
        display:'flex',
        height:'100%'
      }}>
        {capArr.map((e, index)=>
          (<img key={index} src={e} className='fashion_item'/>)
        )}
      </div>
    </div>
  )
}

export default Cap
