import './firstPage.css'
import React, { useEffect, useRef, useState } from 'react'
import { Link } from 'react-router-dom';

import rat from '../img/짭쥐 표지-15.png';
import cat from '../img/짭쥐 표지-16.png';
import img2 from '../img/짭쥐 표지-17.png';
import img3 from '../img/짭쥐 표지-18.png';
import img4 from '../img/짭쥐 표지-19.png';
import icon1 from '../img/map.png';
import icon2 from '../img/book.png';
import icon3 from '../img/clothes.png';
import icon4 from '../img/cooking.png';
import icon5 from '../img/news.png';

const FirstPage = () => {

  

  let [imgAlpha, setImgAlpha] = useState(0);
  let [maxScroll, setMaxScroll] = useState(1);
  let [center, setCenter] = useState(0);

  // 
  const nameTop = useRef(); // 난 너를 사로짭쥐 DOM
  const [imgShape, setImgShape] = useState(); // 짭쥐 크기(높이)

  // maxScroll 설정
  useEffect(()=>{
    setCenter(window.innerHeight);
    window.scrollTo(0,1000);
    setMaxScroll(window.scrollY);
    window.scrollTo(0,0);
    console.log(maxScroll);
    console.log(window.innerHeight);
  },[])

  // imgAlpha(scrollY) 반환
  window.addEventListener('scroll', ()=>{
    setImgAlpha(window.scrollY);
    setImgShape(nameTop.current.clientHeight/0.4);
    console.log(imgShape);
  })

  // text 위치 변경
  const textScroll = {
    top:imgShape
  }
  return (
    <div className='startPage'>
        {/* 첫페이지 */}
        <div className='firstPage'> 
          <img className='firstImg' src={img2} style={{opacity:(1-imgAlpha/maxScroll)}} ref={imgshape}/>
          <img className='firstImg' src={img3} style={textScroll} ref={nameTop}/>
          <img className='firstImg' src={img4} style={{opacity:(1-imgAlpha/maxScroll)}}/>
          <img className='firstImg' src={cat} style={{opacity:(1-imgAlpha/maxScroll)}}/>
          <img className='firstImg' src={rat} style={{opacity:(1-imgAlpha/maxScroll)}}/>
        </div>
        {/* 아이콘 페이지 */}
        <div className='secondPage' style={{opacity:(imgAlpha/maxScroll)}}>
          <Link to='/Main/traval'>
            <img className='secondImg' src={icon1}/>
          </Link>
          <Link to='/Main/book'>
            <img className='secondImg' src={icon2}/>
          </Link>
          <Link to={'/Main/fashion'}>
            <img className='secondImg' src={icon3}/>
          </Link>
          <Link to={'/Main/cook'}>
            <img className='secondImg' src={icon4}/>
          </Link>
          <Link to={'/Main/news'}>
            <img className='secondImg' src={icon5}/>
          </Link>
        </div>
    </div>
  )
}

export default FirstPage
