import React, {useState, useRef } from 'react';
import Cap from '../components/cloth/Cap.jsx';
import Top from '../components/cloth/Top.jsx';
import Bottom from '../components/cloth/Bottom.jsx';
import Shoes from '../components/cloth/Shoes.jsx';
import Outer from '../components/cloth/Outer.jsx';

const M_fashion = () => {
  const max_height = window.innerHeight-100

  const cap_height = max_height*0.2
  const top_height = max_height*0.2
  const bottom_height = max_height*0.2
  const shoes_height = max_height*0.2
  const outer_height = max_height*0.2

  const [left,setLeft] = useState(0);
  const [dragging,setDragging] = useState(false);
  // class에 삼항연산자로 넣어서 드래그할 때 스타일을 따로 넣을 수 있다.
  const dragItem = useRef();
  const dragNode = useRef();
  let Xstart = 0;
  let Xend = 0;

  const handleDragStart = (e) =>{
    console.log('drag starting', e.screenX);
    Xstart = e.screenX;
    setDragging(true);
    dragNode.current = e.target;
    dragNode.current.addEventListener('dragend',handleDragEnd);

    
  }

  const handleDragEnd = (e) =>{
    console.log('drag ending..', e.screenX);
    Xend = e.screenX;
    setLeft(Xend-Xstart);
    setDragging(false);
    dragNode.current.removeEventListener('dragend', handleDragEnd);
    dragNode.current = null;
  }

  const style = {
    height:'100%',
    width:'100%',
    position: 'absolute',
    left:left
  }

  return (
    <div className='center'>
      <div className='fashion_range' style={{
        height:max_height
        }}>
        <div className='fashion_name'/>
        <div className='fashion' style={{
          height:cap_height
        }}>
          <Cap
          draggable 
          onDragStart={(e) => {handleDragStart(e)}} 
          style={style}
          />
        </div>
        <div className='fashion' style={{
          height:top_height
        }}>
          <Top/>
        </div>
        <div className='fashion_name'/>
        <div className='fashion_name'/>
        <div className='fashion' style={{
          height:bottom_height
        }}>
          <Bottom/>
        </div>
        <div className='fashion' style={{
          height:shoes_height
        }}>
          <Shoes/>
        </div>
        <div className='fashion_name'/>
        <div className='fashion_name'/>
        <div className='fashion' style={{
          height:outer_height
        }}>
          <Outer/>
        </div>
      </div>
    </div>
  )
}

export default M_fashion
