import React, {useRef} from 'react'

const Cap = () => {

  const array = []
  for(let i=1;i<91;i++){
    array.push(i)
  }
  const capArr = array.map((e)=>{
    return require(`../../img/패션img/모자/모자${e}.jpg`)
  })

  const handleDragStart = (e) =>{
    console.log('drag starting');
  }


  return (
    <div style={{position:'relative'}}>
      <div draggable onDragStart={handleDragStart} style={{
        height:'100%',
        width:'100%',
        position: 'absolute'
      }}/>
      <div style={{
        display:'flex',
        height:'100%'
      }}>
        {capArr.map((e, index)=>
          (<img key={index} src={e} className='fashion_item'/>)
        )}
      </div>
    </div>
  )
}

export default Cap
